      out.write("        // <span> 태그(X 버튼)를 클릭하면 Modal이 닫습니다.\r\n");
      out.write("        spanes[num].onclick = function() {\r\n");
      out.write("            modals[num].style.display = \"none\";\r\n");
      out.write("        };\r\n");
      out.write("      };\r\n");
      out.write("    }\r\n");
